
$(document).ready(function(){$.getJSON("https://intertwined-doses.000webhostapp.com/products.json",function(data){data=typeof data=='string'?JSON.parse(data):data;data.forEach(function(produtos){name=produtos.name;img=produtos.images[0].imageUrl;value=(produtos.Value).toFixed(2);$('.produtos').append('<div class="products"><img src="'+img+'" alt="" /><div class="text"><p>'+name+'</p><p>R$ <span class="value">'+value+'</span></p></div></div>')})
$('.products').click(function(){valor=$('.value',this).text();$('.compras').remove();$('.buy').append('<p class="compras">R$ '+valor+'</p>');})})
$('.mobile').click(function(){$('.desktop').toggle();})
$('.carrinho').click(function(){$('.active').toggle();})
$('.active').click(function(){$('.compras').remove()})});